#!/usr/bin/python# -*- coding: UTF_8 -*-import randomfrom abc import ABCMeta, abstractmethod#this is the base class that all brain classes should be derived fromclass BaseBrain(object):    __metacalss__ = ABCMeta        @abstractmethod    def check(self):        pass    @abstractmethod    def bet(self):        pass    @abstractmethod    def rase(self):        pass        @abstractmethod    def fold(self):        passclass allIn():    """This is temporarily something which will make a player randomly leave a table."""    def __init__(self, wantToJoinAGame=True, wantToLeaveTheTable=False):        self.wantToJoinAGame = wantToJoinAGame        self.wantToLeaveTheTable = wantToLeaveTheTable    def tossACoin(self):        return bool(random.getrandbits(1))if __name__ == "__main__":    pass